import react, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import NavBar from "./NavBar";
import { Outlet } from "react-router-dom";
const container = document.getElementById("example");

const PuppyBowl = () => {
  return (
    <div>
      <h1> Welcome to our React Router Demo</h1>
      <Navbar />

      {/* This section should show the content of each of the paths of our website */}
      <p>Our content will be shown below: </p>
      <Outlet context={[puppies, setPuppies]} />
      {/* The Outlet component can shapeshift into 3 different components: About, Settings, or AllPuppies. */}

      {/* Note: The Outlet component is going to render the content of each path relative to the component that is connected to it.  */}
      {/* For example:
                    If the path name is ellesWebsite.com/puppies, then the puppies component will render where the Outlet component is.
                    Or if the path name is ellesWebsite.com/settings, then the settings component will replace the puppies component where the Outlet component is. 
                */}
    </div>
  );
  //set up puppy state
  const [puppyPlayers, setPuppyPlayers] = useState([]);

  //STEP 2 fetch data using useEffect hook
  // function exampleFunc() {
  //   console.log("I am an example func");
  // }

  useEffect(() => {
    async function fetchPuppyData() {
      try {
        const response = await fetch(
          "https://fsa-puppy-bowl.herokuapp.com/api/2209-ftb-mt-web-ft/players"
        );

        const puppyData = await response.json();

        setPuppyPlayers(puppyData.data.players);
      } catch (error) {
        console.log(error);
      }
    }
    fetchPuppyData();
  }, []);
  console.log(puppyPlayers);
  return (
    <div id="container">
      <div id="players">
        {puppyPlayers && puppyPlayers.length
          ? puppyPlayers.map((players, index) => {
              return (
                <div class="dogs">
                  <div class="names">{players.name}</div>
                  <div class="breeds">{players.breed}</div>
                  <img class="image" src={players.imageUrl} />
                </div>
              );
            })
          : "bruh"}
      </div>
    </div>
  );
};

ReactDOM.render(<PuppyBowl />, container);
